mapper.select:
SELECT P.ID, P.POST_TITLE, P.POST_CONTENT, P.POST_READ_COUNT, P.MEMBER_ID, P.CREATED_DATE, P.UPDATED_DATE, P.MEMBER_NAME FROM ( SELECT ROWNUM R, P.ID, P.POST_TITLE, P.POST_CONTENT, P.POST_READ_COUNT, P.MEMBER_ID, P.CREATED_DATE, P.UPDATED_DATE, M.MEMBER_NAME FROM TBL_MEMBER M JOIN TBL_POST P
ON M.ID = P.MEMBER_ID AND ROWNUM <= #{endRow}
ORDER BY P.ID DESC ) P WHERE P.R >= #{startRow}

mapper.update:
UPDATE TBL_POST
SET POST_TITLE = #{postTitle}, POST_CONTENT = #{postContent}, UPDATED_DATE = SYSDATE
WHERE ID = #{id}
